SJ.ParalaxBackground=class{constructor(){this._layers=[];let sc=.44,step=.2;const layersCount=SJ.V.texturesNames.background.length;for(let i=0;i<layersCount;i++){const pos={x:0,y:100},layer=new SJ.ParalaxLayer(sc,pos,i);this._layers.push(layer),sc=lerp(sc,1,.2)}this._layers.reverse()}draw(){const cameraPos=SJ.camera.getPosition();this._layers.forEach(layer=>{layer.draw(cameraPos)})}};