SJ.Camera=class{constructor(scaleMod){this._offset=createVector(-200,0),this._offset.x+=.5*SJ.SCREEN_WIDTH,this._offset.y+=.5*SJ.SCREEN_HEIGHT,this._scale=scaleMod,this._targetPosition=createVector(0,0),this.startFollowingJumper()}update(){this._isFollowingJumper&&(this._targetPosition.x=SJ.jumper.body.position.x,this._targetPosition.y=SJ.jumper.body.position.y)}transform(){const targetPos=this._targetPosition;scale(this._scale),translate(-targetPos.x+this._offset.x/this._scale,-targetPos.y+this._offset.y/this._scale)}stopFollowingJumper(){this._isFollowingJumper=!1}startFollowingJumper(){this._isFollowingJumper=!0}};