function Camera(jumper,scaleMod){this.jumper=jumper,this.offset=createVector(-200,0),this.offset.x+=.5*width,this.offset.y+=.5*height,this.scale=scaleMod,this.transform=()=>{const targetPos=jumper.body.position;scale(this.scale),translate(-targetPos.x+this.offset.x/this.scale,-targetPos.y+this.offset.y/this.scale)}}